				jg_obj.fillEllipse(x1, y1, w, h);
			}
			if (this.fgcolor != "none") {
				jg_obj.setColor(this.fgcolor);
				jg_obj.setStroke(this.linewidth);
				jg_obj.drawEllipse(x1, y1,  w - this.linewidth,h - this.linewidth);
		    }
		};

		this.drawLine = function (x1,y1,x2,y2) {
