MMCanvas.prototype.getWhere = function (who){
      
    	var T= 0, L= 0;
    	while(who!=null){
    	  if(who.offsetWidth!=undefined) {
      		L += parseInt(who.offsetLeft);
      		T += parseInt(who.offsetTop);
      		who = who.offsetParent;
      	}
    	}
    	return {'left':L, 'top':T};
}

