            }
          }
          
          try {
            this.centerlat = linearray[0].centerlat;
            this.centerlng = linearray[0].centerlong;
          }
          catch(e) {}
          
          //this.moveToMe();
        }
      }
    }

    if(this.objectdozoom == 1) {
      mmmap.setZoom(mmmap.findAppropriateZoom(this.allobjectpoints, true));
    }
  }

