				this.updateVisibleLatLong();

				var visiblex = parseInt((current_center_x + this.mapoffset.x)/ imagewidth);
				var visibley = parseInt((current_center_y + this.mapoffset.y)/ imageheight);

				var visible  = visiblex + visibley*resolution;

