							else {
								if ((!obj.div.offsetHeight || obj.div.offsetHeight == 0) && obj.height) {
									tmptop =  tmptop - obj.height / 2;
								} else {
									tmptop = tmptop - obj.div.offsetHeight / 2;
								}
