      this.getAddressFullTextFromLatLon = function(lat, lon, lang, varname, callback) { // return string
        if(!varname || typeof(varname) != 'string' || !callback || typeof(callback) != 'string') {
          return ;
        }
        else {         
          this.addressText = false;
