		newxs[i]  = longToPoint(xs[i],resolution) - longToPoint(xs[0],resolution);
		newys[i]  = latToPoint(ys[i],resolution) - latToPoint(ys[0],resolution);

		newxs_border[i] = newxs[i] - 2;
		newys_border[i] = newys[i] - 2;
